"use client";

import { useState, useEffect } from "react";
import {
  View,
  Text,
  FlatList,
  Image,
  TouchableOpacity,
  StyleSheet,
  Alert,
} from "react-native";
import { supabase } from "../lib/supabase";
import type { Post, Profile } from "../types";

export default function ProfileScreen() {
  const [profile, setProfile] = useState<Profile | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<"posts" | "pinned">("posts");
  const [pinnedPosts, setPinnedPosts] = useState<Post[]>([]);

  useEffect(() => {
    fetchProfile();
    fetchUserPosts();
    fetchPinnedPosts();
  }, []);

  async function fetchProfile() {
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from("profiles")
        .select("*")
        .eq("id", user.id)
        .single();

      if (error) throw error;
      setProfile(data);
    } catch (error: any) {
      Alert.alert("Error", error.message);
    }
  }

  async function fetchUserPosts() {
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from("posts")
        .select(
          `
          *,
          likes (id),
          comments (id)
        `
        )
        .eq("user_id", user.id)
        .order("created_at", { ascending: false });

      if (error) throw error;

      const postsWithCounts =
        data?.map((post) => ({
          ...post,
          likes_count: post.likes?.length || 0,
          comments_count: post.comments?.length || 0,
        })) || [];

      setPosts(postsWithCounts);
    } catch (error: any) {
      Alert.alert("Error", error.message);
    } finally {
      setLoading(false);
    }
  }

  async function fetchPinnedPosts() {
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from("pins")
        .select(
          `
        *,
        posts (
          *,
          profiles (
            id,
            username,
            full_name
          )
        )
      `
        )
        .eq("user_id", user.id)
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Transform the data to match Post interface
      const transformedPosts =
        data?.map((pin) => ({
          ...pin.posts,
          profiles: pin.posts.profiles,
          is_pinned: true,
          pinned_at: pin.created_at,
        })) || [];

      setPinnedPosts(transformedPosts);
    } catch (error: any) {
      Alert.alert("Error", error.message);
    }
  }

  async function signOut() {
    const { error } = await supabase.auth.signOut();
    if (error) Alert.alert("Error", error.message);
  }

  async function deletePost(postId: string) {
    Alert.alert("Delete Post", "Are you sure you want to delete this post?", [
      { text: "Cancel", style: "cancel" },
      {
        text: "Delete",
        style: "destructive",
        onPress: async () => {
          try {
            const { error } = await supabase
              .from("posts")
              .delete()
              .eq("id", postId);

            if (error) throw error;

            fetchUserPosts();
            Alert.alert("Success", "Post deleted successfully");
          } catch (error: any) {
            Alert.alert("Error", error.message);
          }
        },
      },
    ]);
  }

  async function unpinPost(postId: string) {
    Alert.alert("Unpin Post", "Remove this post from your pinned collection?", [
      { text: "Cancel", style: "cancel" },
      {
        text: "Unpin",
        style: "destructive",
        onPress: async () => {
          try {
            const {
              data: { user },
            } = await supabase.auth.getUser();
            if (!user) throw new Error("No user found");

            const { error } = await supabase
              .from("pins")
              .delete()
              .eq("post_id", postId)
              .eq("user_id", user.id);

            if (error) throw error;

            fetchPinnedPosts();
            Alert.alert("Success", "Post unpinned successfully");
          } catch (error: any) {
            Alert.alert("Error", error.message);
          }
        },
      },
    ]);
  }

  const renderPost = ({ item }: { item: Post }) => (
    <TouchableOpacity
      style={styles.postItem}
      onLongPress={() => deletePost(item.id)}
    >
      <Image source={{ uri: item.image_url }} style={styles.postImage} />
      <View style={styles.postOverlay}>
        <Text style={styles.postTitle} numberOfLines={2}>
          {item.title}
        </Text>
        <View style={styles.postStats}>
          <Text style={styles.statText}>‚ù§Ô∏è {item.likes_count}</Text>
          <Text style={styles.statText}>üí¨ {item.comments_count}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );

  const renderPinnedPost = ({ item }: { item: Post }) => (
    <TouchableOpacity
      style={styles.postItem}
      onLongPress={() => unpinPost(item.id)}
    >
      <Image source={{ uri: item.image_url }} style={styles.postImage} />
      <View style={styles.postOverlay}>
        <View style={styles.pinnedIndicator}>
          <Text style={styles.pinnedIcon}>üìå</Text>
        </View>
        <Text style={styles.postTitle} numberOfLines={2}>
          {item.title}
        </Text>
        <Text style={styles.postAuthor} numberOfLines={1}>
          by @{item.profiles?.username}
        </Text>
      </View>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.centered}>
        <Text>Loading...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <View style={styles.profileInfo}>
          <View style={styles.avatar}>
            <Text style={styles.avatarText}>
              {profile?.username?.charAt(0).toUpperCase()}
            </Text>
          </View>
          <View style={styles.userDetails}>
            <Text style={styles.fullName}>{profile?.full_name}</Text>
            <Text style={styles.username}>@{profile?.username}</Text>
            <Text style={styles.postCount}>{posts.length} posts</Text>
          </View>
        </View>

        <TouchableOpacity style={styles.signOutButton} onPress={signOut}>
          <Text style={styles.signOutText}>Sign Out</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.tabContainer}>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === "posts" && styles.activeTab]}
          onPress={() => setActiveTab("posts")}
        >
          <Text
            style={[
              styles.tabText,
              activeTab === "posts" && styles.activeTabText,
            ]}
          >
            My Posts ({posts.length})
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.tabButton, activeTab === "pinned" && styles.activeTab]}
          onPress={() => setActiveTab("pinned")}
        >
          <Text
            style={[
              styles.tabText,
              activeTab === "pinned" && styles.activeTabText,
            ]}
          >
            Pinned ({pinnedPosts.length})
          </Text>
        </TouchableOpacity>
      </View>

      {activeTab === "posts" ? (
        <>
          <Text style={styles.sectionTitle}>My Posts</Text>
          {posts.length === 0 ? (
            <View style={styles.emptyState}>
              <Text style={styles.emptyText}>No posts yet</Text>
              <Text style={styles.emptySubtext}>
                Start sharing your photos!
              </Text>
            </View>
          ) : (
            <FlatList
              data={posts}
              renderItem={renderPost}
              keyExtractor={(item) => item.id}
              numColumns={2}
              showsVerticalScrollIndicator={false}
              contentContainerStyle={styles.postsGrid}
            />
          )}
        </>
      ) : (
        <>
          <Text style={styles.sectionTitle}>Pinned Posts</Text>
          {pinnedPosts.length === 0 ? (
            <View style={styles.emptyState}>
              <Text style={styles.emptyText}>No pinned posts yet</Text>
              <Text style={styles.emptySubtext}>
                Pin posts you love to save them here!
              </Text>
            </View>
          ) : (
            <FlatList
              data={pinnedPosts}
              renderItem={({ item }) => renderPinnedPost({ item })}
              keyExtractor={(item) => `pinned-${item.id}`}
              numColumns={2}
              showsVerticalScrollIndicator={false}
              contentContainerStyle={styles.postsGrid}
            />
          )}
        </>
      )}
    </View>
  );
}

